import React, { useEffect, useState } from 'react';
import './App.css';

interface CreditCard {
  id: number;
  name: string;
  cardNumber: string;
  limit: number;
  balance: number;
}

const API_URL = 'http://localhost:8080/api/cards';

function App() {
  const [cards, setCards] = useState<CreditCard[]>([]);
  const [name, setName] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [limit, setLimit] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchCards = async () => {
    const res = await fetch(API_URL);
    const data = await res.json();
    setCards(data);
  };

  useEffect(() => {
    fetchCards();
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const res = await fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name,
          cardNumber,
          limit: parseFloat(limit),
        }),
      });
      if (!res.ok) {
        setError('Invalid card number or input.');
      } else {
        setName('');
        setCardNumber('');
        setLimit('');
        fetchCards();
      }
    } catch (err) {
      setError('Server error.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>Credit Card System</h1>
      <form className="card-form" onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input value={name} onChange={e => setName(e.target.value)} required />
        </div>
        <div>
          <label>Card Number</label>
          <input
            value={cardNumber}
            onChange={e => setCardNumber(e.target.value)}
            maxLength={19}
            required
            pattern="\d{1,19}"
            title="Card number must be numeric and up to 19 digits"
          />
        </div>
        <div>
          <label>Limit (£)</label>
          <input
            type="number"
            value={limit}
            onChange={e => setLimit(e.target.value)}
            min="0"
            step="0.01"
            required
          />
        </div>
        {error && <div className="error">{error}</div>}
        <button type="submit" disabled={loading}>Add Card</button>
      </form>
      <h2>Cards</h2>
      <table className="card-list">
        <thead>
          <tr>
            <th>Name</th>
            <th>Card Number</th>
            <th>Limit (£)</th>
            <th>Balance (£)</th>
          </tr>
        </thead>
        <tbody>
          {cards.map(card => (
            <tr key={card.id}>
              <td>{card.name}</td>
              <td>{card.cardNumber}</td>
              <td>{card.limit.toFixed(2)}</td>
              <td>{card.balance.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
