import React, { useEffect, useState } from 'react';
import './App.css';

interface CreditCard {
  id: number;
  name: string;
  cardNumber: string;
  creditLimit: number;
  balance: number;
}

interface ErrorResponse {
  message: string;
  field?: string;
}

const API_URL = 'http://localhost:8080/api/cards';

function App() {
  const [cards, setCards] = useState<CreditCard[]>([]);
  const [name, setName] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [limit, setLimit] = useState('');
  const [error, setError] = useState<ErrorResponse | null>(null);
  const [loading, setLoading] = useState(false);

  const validateForm = (): boolean => {
    if (!name.trim()) {
      setError({ message: 'Name is required', field: 'name' });
      return false;
    }
    if (!cardNumber.trim()) {
      setError({ message: 'Card number is required', field: 'cardNumber' });
      return false;
    }
    if (!/^\d{1,19}$/.test(cardNumber)) {
      setError({ message: 'Card number must be numeric and up to 19 digits', field: 'cardNumber' });
      return false;
    }
    if (!limit || parseFloat(limit) <= 0) {
      setError({ message: 'Credit limit must be greater than 0', field: 'limit' });
      return false;
    }
    return true;
  };

  const fetchCards = async () => {
    try {
      const res = await fetch(API_URL);
      const data = await res.json();
      setCards(data);
    } catch (err) {
      setError({ message: 'Failed to fetch cards. Please try again later.' });
    }
  };

  useEffect(() => {
    fetchCards();
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);

    if (!validateForm()) {
      return;
    }

    setLoading(true);
    try {
      const res = await fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name,
          cardNumber,
          creditLimit: parseFloat(limit),
        }),
      });

      if (!res.ok) {
        const errorData: ErrorResponse = await res.json();
        setError(errorData);
      } else {
        setName('');
        setCardNumber('');
        setLimit('');
        fetchCards();
      }
    } catch (err) {
      setError({ message: 'Server error. Please try again later.' });
    } finally {
      setLoading(false);
    }
  };

  const getFieldErrorClass = (fieldName: string) => {
    return error?.field === fieldName ? 'error-field' : '';
  };

  return (
    <div className="container">
      <h1>Credit Card System</h1>
      <form className="card-form" onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input 
            value={name} 
            onChange={e => setName(e.target.value)} 
            required 
            placeholder="Enter cardholder name"
            className={getFieldErrorClass('name')}
          />
        </div>
        <div>
          <label>Card Number</label>
          <input
            value={cardNumber}
            onChange={e => setCardNumber(e.target.value)}
            maxLength={19}
            required
            pattern="\d{1,19}"
            title="Card number must be numeric and up to 19 digits"
            placeholder="Enter card number"
            className={getFieldErrorClass('cardNumber')}
          />
        </div>
        <div>
          <label>Limit (£)</label>
          <input
            type="number"
            value={limit}
            onChange={e => setLimit(e.target.value)}
            min="0"
            step="0.01"
            required
            placeholder="Enter credit limit"
            className={getFieldErrorClass('limit')}
          />
        </div>
        {error && (
          <div className="error">
            <strong>Error:</strong> {error.message}
          </div>
        )}
        <button type="submit" disabled={loading}>
          {loading ? 'Adding Card...' : 'Add Card'}
        </button>
      </form>
      <h2>Cards</h2>
      {cards.length === 0 ? (
        <p className="no-cards">No cards added yet.</p>
      ) : (
        <table className="card-list">
          <thead>
            <tr>
              <th>Name</th>
              <th>Card Number</th>
              <th>Limit (£)</th>
              <th>Balance (£)</th>
            </tr>
          </thead>
          <tbody>
            {cards.map(card => (
              <tr key={card.id}>
                <td>{card.name}</td>
                <td>{maskCardNumber(card.cardNumber)}</td>
                <td>{card.creditLimit.toFixed(2)}</td>
                <td>{card.balance.toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

function maskCardNumber(cardNumber: string): string {
  if (!cardNumber) return '';
  const lastFour = cardNumber.slice(-4);
  return `****-****-****-${lastFour}`;
}

export default App;
